import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './SchedulePage.css';
import ÏûÑÏòÅÏõÖ from '../img/profile/ÏûÑÏòÅÏõÖ.jpg.webp';
import ÍπÄÌò∏Ï§ë from '../img/profile/ÍπÄÌò∏Ï§ë.jpg';
import ÏòÅÌÉÅ from '../img/profile/ÏòÅÌÉÅ.jpeg';
import Ïù¥Ï∞¨Ïõê from '../img/profile/Ïù¥Ï∞¨Ïõê.jpg';
import Î∞ïÏßÄÌòÑ from '../img/profile/Î∞ïÏßÄÌòÑ.webp';

const SchedulePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedDate, setSelectedDate] = useState(new Date());

  // ÏÉòÌîå ÏïÑÌã∞Ïä§Ìä∏ Îç∞Ïù¥ÌÑ∞
  const artistData = {
    1: { name: 'ÏûÑÏòÅÏõÖ', image: `${ÏûÑÏòÅÏõÖ}` },
    2: { name: 'ÍπÄÌò∏Ï§ë', image: `${ÍπÄÌò∏Ï§ë}` },
    3: { name: 'Î∞ïÏßÄÌòÑ', image: `${Î∞ïÏßÄÌòÑ}` },
    4: { name: 'ÏòÅÌÉÅ', image: `${ÏòÅÌÉÅ}` },
    5: { name: 'Ïù¥Ï∞¨Ïõê', image: `${Ïù¥Ï∞¨Ïõê}` },
  };





  const artist = artistData[id] || { name: 'Unknown Artist', image: 'https://via.placeholder.com/200x200/999/white?text=Unknown' };

  // ÏÉòÌîå ÏùºÏ†ï Îç∞Ïù¥ÌÑ∞ (Ìä∏Î°úÌä∏ Í¥ÄÎ†®)
  const scheduleData = {
    '2024-01-15': { title: 'Ìä∏Î°úÌä∏ ÏΩòÏÑúÌä∏', location: 'Ïò¨Î¶ºÌîΩÍ≥µÏõê', time: '19:00' },
    '2024-01-20': { title: 'Ìå¨ÏÇ¨Ïù∏Ìöå', location: 'ÏΩîÏóëÏä§Î™∞', time: '14:00' },
    '2024-01-25': { title: 'Ïï®Î≤î Î∞úÎß§', location: 'Ïò®ÎùºÏù∏', time: '00:00' },
    '2024-02-01': { title: 'Î∞©ÏÜ° Ï∂úÏó∞', location: 'KBS Ìä∏Î°úÌä∏Ïùò ÎØºÏ°±', time: '20:00' },
    '2024-02-10': { title: 'ÏßÄÎ∞© Í≥µÏó∞', location: 'Î∂ÄÏÇ∞ Ìï¥Ïö¥ÎåÄ', time: '18:00' },
    '2024-02-15': { title: 'Ìå¨ÎØ∏ÌåÖ', location: 'ÏÑúÏö∏ ÏãúÏ≤≠', time: '15:00' },
  };

  const handleBackClick = () => {
    navigate(`/artist/${id}`);
  };

  const formatDate = (date) => {
    return date.toISOString().split('T')[0];
  };

  const getCurrentMonthDates = () => {
    const year = selectedDate.getFullYear();
    const month = selectedDate.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const dates = [];

    // Ïù¥Ï†Ñ Îã¨Ïùò ÎßàÏßÄÎßâ ÎÇ†Îì§
    for (let i = 0; i < firstDay.getDay(); i++) {
      dates.push(null);
    }

    // ÌòÑÏû¨ Îã¨Ïùò ÎÇ†Îì§
    for (let i = 1; i <= lastDay.getDate(); i++) {
      dates.push(new Date(year, month, i));
    }

    return dates;
  };

  const handleDateClick = (date) => {
    if (date) {
      setSelectedDate(date);
    }
  };

  const currentDateStr = formatDate(selectedDate);
  const currentSchedule = scheduleData[currentDateStr];

  return (
    <div className="schedule-page">
      <div className="schedule-header">
        <button className="back-button" onClick={handleBackClick}>
          ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
        </button>
        <h1 className="schedule-title">{artist.name} ÏùºÏ†ï</h1>
      </div>

      <div className="schedule-content">
        <div className="artist-info">
          <img src={artist.image} alt={artist.name} className="artist-image" />
          <h2 className="artist-name">{artist.name}</h2>
        </div>

        <div className="calendar-section">
          <div className="calendar">
            <div className="calendar-header">
              <h3>{selectedDate.getFullYear()}ÎÖÑ {selectedDate.getMonth() + 1}Ïõî</h3>
            </div>
            <div className="calendar-grid">
              <div className="calendar-days">
                <div>Ïùº</div>
                <div>Ïõî</div>
                <div>Ìôî</div>
                <div>Ïàò</div>
                <div>Î™©</div>
                <div>Í∏à</div>
                <div>ÌÜ†</div>
              </div>
              <div className="calendar-dates">
                {getCurrentMonthDates().map((date, index) => (
                  <div
                    key={index}
                    className={`calendar-date ${date ? 'has-date' : 'empty'} ${
                      date && formatDate(date) === currentDateStr ? 'selected' : ''
                    } ${date && scheduleData[formatDate(date)] ? 'has-schedule' : ''}`}
                    onClick={() => handleDateClick(date)}
                  >
                    {date ? date.getDate() : ''}
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="schedule-details">
            <h3>ÏÑ†ÌÉùÎêú ÎÇ†Ïßú: {selectedDate.toLocaleDateString('ko-KR')}</h3>
            {currentSchedule ? (
              <div className="schedule-item">
                <h4>{currentSchedule.title}</h4>
                <p>üìç {currentSchedule.location}</p>
                <p>üïê {currentSchedule.time}</p>
              </div>
            ) : (
              <p className="no-schedule">Ìï¥Îãπ ÎÇ†ÏßúÏóê ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SchedulePage;
