import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ChatPage.css';
import logo from '../img/logo.png';
import ÏûÑÏòÅÏõÖ from '../img/profile/ÏûÑÏòÅÏõÖ.jpg.webp';
import ÍπÄÌò∏Ï§ë from '../img/profile/ÍπÄÌò∏Ï§ë.jpg';
import ÏòÅÌÉÅ from '../img/profile/ÏòÅÌÉÅ.jpeg';
import Ïù¥Ï∞¨Ïõê from '../img/profile/Ïù¥Ï∞¨Ïõê.jpg';
import Î∞ïÏßÄÌòÑ from '../img/profile/Î∞ïÏßÄÌòÑ.webp';

const ChatPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);

  // ÏÉòÌîå ÏïÑÌã∞Ïä§Ìä∏ Îç∞Ïù¥ÌÑ∞
  const artistData = {
    1: { name: 'ÏûÑÏòÅÏõÖ', image: `${ÏûÑÏòÅÏõÖ}` },
    2: { name: 'ÍπÄÌò∏Ï§ë', image: `${ÍπÄÌò∏Ï§ë}` },
    3: { name: 'Î∞ïÏßÄÌòÑ', image: `${Î∞ïÏßÄÌòÑ}` },
    4: { name: 'ÏòÅÌÉÅ', image: `${ÏòÅÌÉÅ}` },
    5: { name: 'Ïù¥Ï∞¨Ïõê', image: `${Ïù¥Ï∞¨Ïõê}` },
  };

  const artist = artistData[id] || { name: 'Unknown Artist', image: 'https://via.placeholder.com/50x50/999/white?text=Unknown' };

  // ÏÉòÌîå Ï¥àÍ∏∞ Î©îÏãúÏßÄ (Ìä∏Î°úÌä∏ Í¥ÄÎ†®)
  const initialMessages = [
    { id: 1, text: 'ÏïàÎÖïÌïòÏÑ∏Ïöî! Ìä∏Î°úÌä∏ Ìå¨ Ïó¨Îü¨Î∂ÑÎì§ Î∞òÍ∞ëÏäµÎãàÎã§! üéµ', sender: 'artist', timestamp: '10:00' },
    { id: 2, text: 'ÏïàÎÖïÌïòÏÑ∏Ïöî! Ïò§ÎäòÎèÑ Ï¢ãÏùÄ ÎÖ∏Îûò Îì§Î†§Ï£ºÏÑ∏Ïöî!', sender: 'user', timestamp: '10:01' },
    { id: 3, text: 'ÏÉà Ìä∏Î°úÌä∏ Í≥° Ï§ÄÎπÑ Ï§ëÏù¥ÏóêÏöî. Í∏∞ÎåÄÌï¥Ï£ºÏÑ∏Ïöî!', sender: 'artist', timestamp: '10:02' },
    { id: 4, text: 'Ï†ïÎßê Í∏∞ÎåÄÎê©ÎãàÎã§! Ïñ∏Ï†ú ÎÇòÏò§ÎÇòÏöî?', sender: 'user', timestamp: '10:03' },
  ];

  useEffect(() => {
    setMessages(initialMessages);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleBackClick = () => {
    navigate(`/artist/${id}`);
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      const message = {
        id: messages.length + 1,
        text: newMessage,
        sender: 'user',
        timestamp: new Date().toLocaleTimeString('ko-KR', { hour: '2-digit', minute: '2-digit' })
      };
      setMessages([...messages, message]);
      setNewMessage('');

      // ÏïÑÌã∞Ïä§Ìä∏ ÏùëÎãµ ÏãúÎÆ¨Î†àÏù¥ÏÖò (Ìä∏Î°úÌä∏ Í¥ÄÎ†®)
      setTimeout(() => {
        const artistResponse = {
          id: messages.length + 2,
          text: 'Î©îÏãúÏßÄ Í∞êÏÇ¨Ìï©ÎãàÎã§! Ìä∏Î°úÌä∏Î°ú Îçî Ï¢ãÏùÄ Í≥° Îì§Î†§ÎìúÎ¶¥Í≤åÏöî! üé§',
          sender: 'artist',
          timestamp: new Date().toLocaleTimeString('ko-KR', { hour: '2-digit', minute: '2-digit' })
        };
        setMessages(prev => [...prev, artistResponse]);
      }, 1000);
    }
  };

  return (
    <div className="chat-page">
      <div className="chat-header">
        <button className="back-button" onClick={handleBackClick}>
          ‚Üê Îí§Î°úÍ∞ÄÍ∏∞
        </button>
        <div className="chat-title">
          <img src={artist.image} alt={artist.name} className="artist-avatar" />
          <h1>{artist.name} Ï±ÑÌåÖÎ∞©</h1>
        </div>
      </div>

      <div className="chat-container">
        <div className="messages-container">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender === 'user' ? 'user-message' : 'artist-message'}`}
            >
              <div className="message-content">
                <p className="message-text">{message.text}</p>
                <span className="message-time">{message.timestamp}</span>
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        <form className="message-input-form" onSubmit={handleSendMessage}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
            className="message-input"
          />
          <button type="submit" className="send-button">
            Ï†ÑÏÜ°
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatPage;
